        const date = new Date(unixTime * 1000);

        switch (format) {
            case 'date':
                return moment(date).format('L');
            case 'time':
                return moment(date).format('LT');
            case 'YYYY-MM-DD':
                return moment(date).format('YYYY-MM-DD');
            case 'YYYYMMDD':
                return moment(date).format('YYYYMMDD');
            default:
                return '';
        }
    }

